      <script>
        // Array of hero images for the About page
        const aboutHeroImages = [
          '/img.php?src=group1.jpg&w=1920&h=1080',
          '/img.php?src=group2.jpg&w=1920&h=1080',
          '/img.php?src=group3.jpg&w=1920&h=1080',
        ];
        let currentHeroIdx = 0;
        let autoRotateInterval;
        
        function updateHeroImage() {
          document.getElementById('aboutHeroImg').style.opacity = '0';
          setTimeout(() => {
            document.getElementById('aboutHeroImg').src = aboutHeroImages[currentHeroIdx];
            document.getElementById('aboutHeroImg').style.opacity = '1';
          }, 300);
        }
        
        function rotateAboutHeroImg() {
          currentHeroIdx = (currentHeroIdx + 1) % aboutHeroImages.length;
          updateHeroImage();
        }
        
        function goToPrevImage() {
          currentHeroIdx = (currentHeroIdx - 1 + aboutHeroImages.length) % aboutHeroImages.length;
          updateHeroImage();
          resetAutoRotate();
        }
        
        function goToNextImage() {
          currentHeroIdx = (currentHeroIdx + 1) % aboutHeroImages.length;
          updateHeroImage();
          resetAutoRotate();
        }
        
        function resetAutoRotate() {
          clearInterval(autoRotateInterval);
          autoRotateInterval = setInterval(rotateAboutHeroImg, 3500);
        }
        
        // Start auto-rotation
        autoRotateInterval = setInterval(rotateAboutHeroImg, 3500);
        
        // Add event listeners to buttons
        document.querySelector('.carousel-prev').addEventListener('click', goToPrevImage);
        document.querySelector('.carousel-next').addEventListener('click', goToNextImage);
      </script>
